import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent {

}
<footer class="footer">
  <div class="footer-content">
    <div class="social-media">
      <a href="https://www.facebook.com/"><img src="assets/icons8-facebook-nouveau-100.png" alt="Facebook"></a>
      <a href="https://www.instagram.com/"><img src="assets/icons8-instagram-vieux-logo-100.png" alt="Instagram"></a>
    </div>

  </div>
  <div class="footer-text">
    &copy; Siham Hakem 2023
  </div>
</footer>
